import React, { useState } from 'react';

import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import './Expenses.css';
import ExpensesFilter from './ExpensesFilter';

/** Alternative way to write functions in Javascript.

** ES6 **
const App = () =>  {
   const expenses = [ 
     .
     .
   ]
}
*/

const Expenses = (props) => {
const [filteredYear, setFilteredYear] = useState('2022');

  const filterChangeHandler =  selectedYear => {
    setFilteredYear(selectedYear);
    // console.log('Expenses.js');
    // console.log(selectedYear);
  };
  return (
      <div>
        <Card className="expenses">
        <ExpensesFilter selected={filteredYear} onChangeFilter={filterChangeHandler} /> 
        {props.items.map(x=x*2)} {/** the map() method creates a new array populated 
                            with the results of calling a provided function on every element
  in the calling array*/}  
            <ExpenseItem 
            /** When we chance the name of props we need to change here as well; for example Expenses.js line 19. */
            title={props.items[0].title} 
            amount={props.items[0].amount} 
            date={props.items[0].date}
            />
            <ExpenseItem 
            title={props.items[1].title} 
            amount={props.items[1].amount} 
            date={props.items[1].date}
            />
            <ExpenseItem 
            title={props.items[2].title} 
            amount={props.items[2].amount} 
            date={props.items[2].date}
            />
            <ExpenseItem 
            title={props.items[3].title} 
            amount={props.items[3].amount} 
            date={props.items[3].date}
            />
         </Card>
      </div>
  );
}

export default Expenses;
